import React, { useRef } from "react";
import {
  FaBars,
  FaGamepad,
  FaHome,
  FaQuestion,
  FaSpeakap,
  FaTimes,
} from "react-icons/fa";
import "../components/Navbar.css";

function Navbar() {
  const navRef = useRef();

  const showNavbar = () => {
    const navbar = navRef.current;

    navbar.classList.toggle("responsive_nav");

    // Check if the navbar is currently expanded
    const isExpanded = navbar.classList.contains("responsive_nav");

    // If the navbar is expanded, add CSS styles to make it position fixed
    if (isExpanded) {
      navbar.style.position = "fixed";
      navbar.style.top = "0";
      navbar.style.left = "0";
      navbar.style.right = "0";
    } else {
      // If the navbar is not expanded, remove the added styles
      navbar.style.position = "";
      navbar.style.top = "";
      navbar.style.left = "";
      navbar.style.right = "";
    }
  };


  return (
    <header>
      <h3>Logo</h3>
      <nav ref={navRef}>
        <a href="/#">
          <FaHome /> Home
        </a>
        <a href="/#">
          <FaGamepad /> Spark
        </a>
        <a href="/#">
          <FaQuestion /> FAQ
        </a>
        <a href="/#">
          <FaSpeakap /> Forum
        </a>
        <button className="nav-btn nav-close-btn" onClick={showNavbar}>
          <FaTimes />
        </button>
      </nav>
      <button className="nav-btn" onClick={showNavbar}>
        <FaBars />
      </button>
    </header>
  );
}

export default Navbar;
